      )
    values (#{id,jdbcType=INTEGER}, #{goodsname,jdbcType=VARCHAR}, #{goodsno,jdbcType=VARCHAR}, 
      #{weight,jdbcType=VARCHAR}, #{goodsamount,jdbcType=VARCHAR}, #{unitprice,jdbcType=VARCHAR}, 
      #{totalprice,jdbcType=VARCHAR}, #{orderno,jdbcType=INTEGER}, #{createtime,jdbcType=VARCHAR},#{specifications,jdbcType=VARCHAR}
      )

mapper.select:
select * from t_order_out_bs 
where id in 

	${id}

mapper.insert:
	insert into t_order_out_sku_bs (id, goodsName, goodsNo, 
      weight, goodsAmount, unitPrice, 
      totalPrice, orderno, createTime,specifications,netWeight
      )	   
    values 

    (#{orderOutSkuBS.id,jdbcType=INTEGER}, #{orderOutSkuBS.goodsname,jdbcType=VARCHAR}, #{orderOutSkuBS.goodsno,jdbcType=VARCHAR}, 
      #{orderOutSkuBS.weight,jdbcType=VARCHAR}, #{orderOutSkuBS.goodsamount,jdbcType=VARCHAR}, #{orderOutSkuBS.unitprice,jdbcType=VARCHAR}, 
      #{orderOutSkuBS.totalprice,jdbcType=VARCHAR}, #{orderOutSkuBS.orderno,jdbcType=INTEGER}, #{orderOutSkuBS.createtime,jdbcType=VARCHAR},
      #{orderOutSkuBS.specifications,jdbcType=VARCHAR},#{orderOutSkuBS.netweight,jdbcType=VARCHAR}
      )

mapper.delete:
  	  delete from t_order_out_sku_bs where orderno in
  	  (select orderno from t_order_out_bs where id in

		${id}

  	  )

mapper.select:
select t.orderNo,t.consignee,t.consigneeAddress,t.grossWeight,t.consigneeTel,t.totalWayBill,t.wayBillNo,t.rate,t.destinationCountry,ts.netweight,ts.weight,ts.goodsName,ts.goodsNo,ts.specifications,ts.goodsAmount,ts.unitPrice,ts.totalPrice,t.currency,t.itemCode,t.createTime from t_order_out_bs t left join t_order_out_sku_bs ts on t.orderno =ts.orderno where t.id in
${id}
order by createtime desc,orderno
